<?php

if (! function_exists('laraflash')) {
    /**
     * Helper function to add notification to LaraTrust object.
     *
     * @param  string|null      $notification
     * @param  array|null       $options
     * @return \DevMarketer\LaraFlash\LaraFlash
     */
    function laraflash($notification = null, $options = null)
    {
        $laraflash = app('laraflash');
        if (! is_null($notification)) {
          return $laraflash->add($notification, $options);
        }
        return $laraflash;
    }
}

if (! function_exists('flash')) {
		/**
		 * Alias for "laraflash()" helper function
		 *
		 * @param  string|null      $notification
		 * @param  array|null       $options
		 * @return \DevMarketer\LaraFlash\LaraFlash
		 */
		function flash($notification = null, $options = null)
 		{
 				return laraflash($notification, $options);
 		}
}
